import React, { useEffect } from "react";
import { useTheme } from "@mui/material/styles";
import { Box, IconButton } from "@mui/material";
import SkipPreviousIcon from "@mui/icons-material/SkipPrevious";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import SkipNextIcon from "@mui/icons-material/SkipNext";
import { useAudioPlayer } from "../hooks/useAudioPlayer";
import { Track } from "../api/track/models/Track";

interface PlayTrackProps {
  track: Track;
  player: ReturnType<typeof useAudioPlayer>;
}

export const PlayTrack: React.FC<PlayTrackProps> = ({ track, player }) => {
  const { audioRef, isPlaying, currentTrackId, togglePlay } = player;

  useEffect(() => {
    if (audioRef.current && currentTrackId === track.id && track.audioFile) {
      audioRef.current.src = track.audioFile;
      if (isPlaying) {
        audioRef.current.load();
        audioRef.current.oncanplaythrough = () => {
          audioRef.current
            ?.play()
            .catch((err) => console.error("Play error:", err));
        };
      }
    }
  }, [track.audioFile, currentTrackId, isPlaying]);

  return (
    <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
      <audio ref={audioRef} style={{ width: "100%", borderRadius: 8 }} />
      <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
        <IconButton
          onClick={() =>
            track.audioFile && togglePlay(track.id, track.audioFile)
          }
        >
          {currentTrackId === track.id && isPlaying ? (
            <PauseIcon sx={{ height: 38, width: 38 }} />
          ) : (
            <PlayArrowIcon sx={{ height: 38, width: 38 }} />
          )}
        </IconButton>
      </Box>
    </Box>
  );
};
