import React from "react";
import { Track } from "../types/commonTypes";

interface TrackItemProps {
  track: Track;
}

export const TrackItem: React.FC<TrackItemProps> = ({ track }) => {
  return (
    <div className="bg-white rounded-xl shadow hover:shadow-lg transition overflow-hidden border border-gray-200">
      {/* Обкладинка */}
      {track.coverImage && (
        <img
          src={track.coverImage}
          alt={track.title}
          className="w-full h-40 object-cover"
        />
      )}

      {/* Контент */}
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-900 truncate">
          {track.title}
        </h3>
        <p className="text-sm text-gray-600">{track.artist}</p>

        {/* Жанри */}
        <div className="flex flex-wrap gap-2 mt-3">
          {track.genres.map((genre, index) => (
            <span
              key={index}
              className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded-full"
            >
              {genre.name}
            </span>
          ))}
        </div>

        {/* Аудіо плеєр */}
        {track.audioFile && (
          <audio controls className="w-full mt-4 rounded">
            <source src={track.audioFile} type="audio/mpeg" />
            Ваш браузер не підтримує аудіо.
          </audio>
        )}
      </div>
    </div>
  );
};
