  const [tracks, setTracks] = useState<Track[]>([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchTracks()
      .then((res) => {
        setTracks(res.data);
      })
      .catch((err) => console.error("–ü–æ–º–∏–ª–∫–∞:", err));
  }, []);

