import React, { useState } from "react";
import { Track } from "../../api/track/models/Track";
import { useTheme } from "@mui/material/styles";
import {
  Box,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  Typography,
  Menu,
  MenuItem,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { useAudioPlayer } from "../../hooks/useAudioPlayer";
import { PlayTrack } from "../PlayTrack";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import { EditTrackModal } from "../EditTrackModal";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackListItem: React.FC<TrackItemProps> = ({
  track,
  onDelete,
  onTrackUpdated,
}) => {
  const theme = useTheme();
  const player = useAudioPlayer();

  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const openMenu = Boolean(anchorEl);

  const [isEditOpen, setIsEditOpen] = useState(false);

  const handleMenuClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleEditClick = () => {
    setIsEditOpen(true);
    handleMenuClose();
  };

  return (
    <Card
      sx={{
        display: "flex",
        alignItems: "center",
        width: "100%",
        minHeight: 80,
        px: 3,
        py: 1.5,
        borderRadius: 3,
        boxShadow: 2,
        backgroundColor: "background.paper",
        "&:hover": {
          backgroundColor: "rgba(244,201,214,0.15)",
          boxShadow: 6,
          transform: "scale(1.01)",
        },
        transition: "all 0.3s ease",
        cursor: "pointer",
      }}
    >
      {/* Обкладинка */}
      <CardMedia
        component="img"
        sx={{
          width: 60,
          height: 60,
          borderRadius: 2,
          flexShrink: 0,
          objectFit: "cover",
          boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
        }}
        image={
          track.coverImage ||
          "https://media.istockphoto.com/id/2167996762/uk/%D1%84%D0%BE%D1%82%D0%BE/flamingos-on-lake.jpg?s=2048x2048&w=is&k=20&c=A_twH4lS0xkDGqh1oSO9aOEREPNEJpdRUkwOwsXN7nE="
        }
        alt={track.title}
      />

      {/* Інформація */}
      <Box sx={{ flexGrow: 1, ml: 3, minWidth: 0 }}>
        <CardContent sx={{ py: 0 }}>
          <Typography
            variant="subtitle1"
            sx={{
              fontWeight: 600,
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              fontSize: "1rem",
            }}
          >
            {track.title}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              mt: 0.25,
              fontSize: "0.875rem",
            }}
          >
            {track.artist}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              mt: 0.25,
              fontSize: "0.8rem",
              fontStyle: "italic",
            }}
          >
            {track.album || "Unknown album"}
          </Typography>
        </CardContent>
      </Box>

      {/* Кнопки керування */}
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          gap: 1.5,
          flexShrink: 0,
        }}
      >
        <PlayTrack key={track.id} track={track} player={player} />
        <IconButton
          color="error"
          sx={{ "&:hover": { backgroundColor: "rgba(244,67,54,0.1)" } }}
          onClick={() => onDelete(track.id)}
        >
          <DeleteIcon />
        </IconButton>
        <IconButton onClick={handleMenuClick}>
          <MoreVertIcon />
        </IconButton>
      </Box>
    </Card>
  );
};
