import React, { useState } from "react";
import { Track } from "../../api/track/models/Track";
import { useTheme } from "@mui/material/styles";
import {
  Box,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  Typography,
  Menu,
  MenuItem,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { useAudioPlayer } from "../../hooks/useAudioPlayer";
import { PlayTrack } from "../PlayTrack";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import { EditTrackModal } from "../EditTrackModal";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackListItem: React.FC<TrackItemProps> = ({
  track,
  onDelete,
  onTrackUpdated,
}) => {
  const theme = useTheme();
  const player = useAudioPlayer();

  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const openMenu = Boolean(anchorEl);

  const [isEditOpen, setIsEditOpen] = useState(false);

  const handleMenuClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleEditClick = () => {
    setIsEditOpen(true);
    handleMenuClose();
  };

  return (
    <Card
      sx={{
        display: "flex",
        alignItems: "center",
        width: "100%",
        minHeight: 70,
        px: 2,
        py: 1,
        borderRadius: 2,
        boxShadow: 1,
        backgroundColor: "background.paper",
        "&:hover": {
          backgroundColor: "action.hover",
          boxShadow: 4,
        },
        transition: "0.3s",
      }}
    >
      {/* Обкладинка */}
      <CardMedia
        component="img"
        sx={{ width: 50, height: 50, borderRadius: 1, flexShrink: 0 }}
        image={
          track.coverImage ||
          "https://media.istockphoto.com/id/2167996762/uk/%D1%84%D0%BE%D1%82%D0%BE/flamingos-on-lake.jpg?s=2048x2048&w=is&k=20&c=A_twH4lS0xkDGqh1oSO9aOEREPNEJpdRUkwOwsXN7nE="
        }
        alt={track.title}
      />
      {/* Інформація */}
      <Box sx={{ flexGrow: 1, ml: 2, minWidth: 0 }}>
        <CardContent sx={{ py: 0 }}>
          <Typography
            variant="subtitle1"
            sx={{
              fontWeight: 500,
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
            }}
          >
            {track.title}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
            }}
          >
            {track.artist}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              overflow: "hidden",
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              mt: 0.25,
            }}
          >
            {track.album || "Unknown album"}
          </Typography>
        </CardContent>

        {/* Аудіоплеєр */}
        {/* {track.audioFile && (
          <audio
            //ref={audioRef}
            controls
            style={{
              width: "100%",
              marginTop: 8,
              borderRadius: 8,
            }}
          >
            <source src="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3" />
            Your browser does not support audio.
          </audio>
        )} */}
      </Box>
      {/* Кнопки керування та видалення */}
      <Box
        sx={{ display: "flex", alignItems: "center", gap: 1, flexShrink: 0 }}
      >
        <PlayTrack key={track.id} track={track} player={player} />
        <IconButton color="error" onClick={() => onDelete(track.id)}>
          <DeleteIcon />
        </IconButton>
        <IconButton onClick={handleMenuClick}>
          <MoreVertIcon />
        </IconButton>
      </Box>
    </Card>
  );
};
