import React from "react";
import { Track } from "../types/commonTypes";

interface TrackItemProps {
  track: Track;
}

export const TrackItem: React.FC<TrackItemProps> = ({ track }) => {
  return (
    <div className="bg-white rounded-2xl overflow-hidden shadow-md hover:shadow-xl transition-shadow duration-300 border border-gray-200">
      {/* Обкладинка */}
      {track.coverImage && (
        <div className="relative group">
          <img
            src={track.coverImage}
            alt={track.title}
            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
          />
          <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-2 text-white text-sm font-semibold">
            {track.title}
          </div>
        </div>
      )}

      {/* Контент */}
      <div className="p-4">
        <p className="text-gray-700 font-medium mb-1">{track.artist}</p>

        {/* Жанри */}
        <div className="flex flex-wrap gap-2 mt-2">
          {track.genres?.map((genre) => (
            <span
              key={genre.id}
              className="text-xs bg-purple-100 text-purple-700 px-2 py-1 rounded-full"
            >
              {genre.name}
            </span>
          ))}
        </div>

        {/* Аудіо плеєр */}
        {track.audioFile && (
          <audio controls className="w-full mt-4 rounded">
            <source src={track.audioFile} type="audio/mpeg" />
            Ваш браузер не підтримує аудіо.
          </audio>
        )}
      </div>
    </div>
  );
};
