import React from "react";
import { Track } from "../../api/track/models/Track";
import {
  Box,
  Typography,
  List,
  ListItem,
  Divider,
  Paper,
  Avatar,
  IconButton,
  ListItemAvatar,
  ListItemText,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { PageList } from "../../types/PageList";
import { TrackListItem } from "./TrackListItem";

interface Props {
  tracks: PageList<Track> | undefined;
  onTrackDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackList: React.FC<Props> = ({
  tracks,
  onTrackDelete,
  onTrackUpdated,
}) => {
  return (
    <Box
      sx={{
        width: "100%",
        maxWidth: 1000,
        margin: "0 auto",
        p: 3,
      }}
    >
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          fontWeight: "bold",
          textAlign: "center",
          mb: 3,
          color: "#3E2723",
        }}
      >
        🎶 List of Tracks
      </Typography>

      <Paper
        elevation={4}
        sx={{
          borderRadius: 3,
          overflow: "hidden",
          background: "linear-gradient(145deg, #fafafa, #f4f4f4)",
        }}
      >
        <List>
          {tracks?.data.map((track, index) => (
            <React.Fragment key={track.id}>
              <ListItem
                secondaryAction={
                  <Box>
                    <IconButton
                      edge="end"
                      onClick={() => onTrackUpdated(track)}
                      sx={{
                        "&:hover": { color: "primary.main" },
                      }}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton
                      edge="end"
                      onClick={() => onTrackDelete(track.id)}
                      sx={{
                        "&:hover": { color: "error.main" },
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Box>
                }
                sx={{
                  px: 2,
                  py: 1.5,
                  transition: "all 0.25s ease",
                  "&:hover": {
                    backgroundColor: "rgba(244,201,214,0.2)",
                  },
                }}
              >
                <ListItemAvatar>
                  <Avatar
                    variant="rounded"
                    src={track.coverImage || undefined}
                    sx={{
                      width: 56,
                      height: 56,
                      bgcolor: "#f4c9d6",
                      fontWeight: "bold",
                    }}
                  >
                    {track.title[0]}
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={track.title}
                  secondary={
                    track.artist + (track.album ? ` • ${track.album}` : "")
                  }
                  primaryTypographyProps={{
                    fontWeight: "bold",
                    fontSize: "1rem",
                    color: "#3E2723",
                  }}
                  secondaryTypographyProps={{
                    color: "text.secondary",
                  }}
                />
              </ListItem>
              {index < (tracks?.data.length ?? 0) - 1 && <Divider />}
            </React.Fragment>
          ))}
        </List>
      </Paper>
    </Box>
  );
};
