import React from "react";
import { useTheme } from "@mui/material/styles";
import { Box, IconButton } from "@mui/material";
import SkipPreviousIcon from "@mui/icons-material/SkipPrevious";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";
import SkipNextIcon from "@mui/icons-material/SkipNext";
import { useAudioPlayer } from "../hooks/useAudioPlayer";
import { Track } from "../api/track/models/Track";

interface PlayTrackProps {
  track: Track;
}

export const PlayTrack: React.FC<PlayTrackProps> = ({ track }) => {
  const { audioRef, isPlaying, togglePlay, skipPrevious, skipNext } =
    useAudioPlayer();
  return (
    <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
      {/* Audio element without controls */}
      <audio ref={audioRef} style={{ width: "100%", borderRadius: 8 }}>
        <source
          src={
            track.audioFile ||
            "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3"
          }
        />
        Your browser does not support audio.
      </audio>

      {/* Custom controls */}
      <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
        <IconButton onClick={skipPrevious}>
          <SkipPreviousIcon />
        </IconButton>

        <IconButton onClick={togglePlay}>
          {isPlaying ? (
            <PauseIcon sx={{ height: 38, width: 38 }} />
          ) : (
            <PlayArrowIcon sx={{ height: 38, width: 38 }} />
          )}
        </IconButton>

        <IconButton onClick={skipNext}>
          <SkipNextIcon />
        </IconButton>
      </Box>
    </Box>
  );
};
