import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { TrackItem } from "../components/TrackItem";
import { fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/CreateTrackModal";
import { TrackList } from "../components/TrackList";
import { Track } from "../api/track/models/Track";

export const TrackPage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks();

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-tr from-indigo-50 via-purple-50 to-pink-50 py-12 px-6 sm:px-12">
      <div className="max-w-6xl mx-auto bg-white shadow-2xl rounded-3xl p-10 sm:p-14">
        <header className="flex flex-col sm:flex-row justify-between items-center mb-10 gap-4 sm:gap-0">
          <h1 className="text-4xl sm:text-5xl font-extrabold text-indigo-700 flex items-center gap-3 select-none">
            üéß Tracks
          </h1>
        </header>

        <CreateTrackModal
        // isOpen={showModal}
        // onClose={() => setShowModal(false)}
        // onTrackCreated={handleTrackCreated}
        />

        {tracks?.data.length === 0 ? (
          <p className="text-center text-gray-400 italic text-lg mt-20 select-none">
            –ù–µ–º–∞—î —Ç—Ä–µ–∫—ñ–≤ üí§
          </p>
        ) : (
          <TrackList tracks={tracks?.data ?? []} />
        )}
      </div>
    </div>
  );
};
