import React from "react";
import { Track } from "../api/track/models/Track";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { fetchTrackbySlug } from "../api/track/trackApi";
import { Box, Card, CardMedia, Typography } from "@mui/material";

export const TrackPage = () => {
  const { slug } = useParams<{ slug: string }>();
  const [track, setTrack] = useState<Track | undefined>();

  useEffect(() => {
    const fetchData = async () => {
      if (slug) {
        const result = await fetchTrackbySlug(slug);
        setTrack(result);
      }
    };
    fetchData();
  }, [slug]);

  if (!track) return <Typography>Loading or track not found</Typography>;

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      bgcolor="#f7f7f7"
      minHeight="100vh"
      p={3}
    >
      <Card
        sx={{
          maxWidth: 700,
          width: "100%",
          borderRadius: 3,
          boxShadow: "0 8px 24px rgba(0,0,0,0.15)",
          overflow: "hidden",
          background: "linear-gradient(to bottom right, #ffffff, #f0f0f0)",
        }}
      >
        <Typography variant="h3">{track.title}</Typography>
        <Typography variant="h5">{track.artist}</Typography>
        <Typography variant="h5">{track.album}</Typography>
        <Typography variant="h5">{track.genres}</Typography>
        <CardMedia
          component="img"
          sx={{
            width: 300,
            height: 300,
            borderRadius: 2,
            flexShrink: 0,
            objectFit: "cover",
            boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
          }}
          image={
            track.coverImage ||
            "https://media.istockphoto.com/id/2167996762/uk/%D1%84%D0%BE%D1%82%D0%BE/flamingos-on-lake.jpg?s=2048x2048&w=is&k=20&c=A_twH4lS0xkDGqh1oSO9aOEREPNEJpdRUkwOwsXN7nE="
          }
          alt={track.title}
        />

        <audio controls src={track.audioFile}></audio>
        <Typography sx={{ mt: 2 }}>{track.createdAt}</Typography>
        <Typography variant="h5">{track.updatedAt}</Typography>
      </Card>
    </Box>
  );
};
