import React, { useState } from "react";
import { createTrack } from "../api/track/trackApi";
import { CreateTrack } from "../api/track/models/CreateTrack";

interface Props {
  onSave: (track: string) => void;
}

export const TrackForm = () => {
  const [title, setTitle] = useState("");
  const [artist, setArtist] = useState("");
  const [album, setAlbum] = useState("");
  const [coverImageUrl, setCoverImageUrl] = useState("");
  const [selectedGenres, setSelectedGenres] = useState<string[]>([]);
  const [genres, setGenres] = useState([]);

  // const { genres, loading } = useGenres();

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!title || !artist) {
      alert("Title and Artist are required.");
      return;
    }

    if (
      coverImageUrl &&
      !/^https?:\/\/.*\.(jpg|jpeg|png|gif)$/.test(coverImageUrl)
    ) {
      alert("Invalid image URL");
      return;
    }
  };

  const handleSave = async (trackData: CreateTrack) => {
    try {
      const newTrack = await createTrack(trackData);
      if (newTrack) {
      }
    } catch (err) {
      console.error("Помилка створення треку:", err);
      alert("Не вдалося створити трек");
    }
  };
  //   onSave({
  //     title,
  //     artist,
  //     album,
  //     genres: selectedGenres,
  //     coverImage: coverImageUrl,
  //   });
  // };

  // const HandleAddGenre = (genre: Genres) => {
  //   setSelectedGenres([...selectedGenres, genre]);
  // };

  // const handleRemoveGenre = (id: number) => {
  //   setSelectedGenres(selectedGenres.filter((g) => g.id !== id));
  // };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Track Title"
        className="w-full border p-2"
        required
      />
      <input
        value={artist}
        onChange={(e) => setArtist(e.target.value)}
        placeholder="Artist"
        className="w-full border p-2"
        required
      />
      <input
        value={album}
        onChange={(e) => setAlbum(e.target.value)}
        placeholder="Album"
        className="w-full border p-2"
      />
      <input
        value={coverImageUrl}
        onChange={(e) => setCoverImageUrl(e.target.value)}
        placeholder="Cover image URL"
        className="w-full border p-2"
      />
      {/* <GenreTags
        selectedGenres={selectedGenres}
        availableGenres={genres}
        onAdd={HandleAddGenre}
        onRemove={handleRemoveGenre}
      /> */}
      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        Save
      </button>
    </form>
  );
};
