import { useEffect, useState } from "react";
import { Track } from "../types/track";
import { TrackItem } from "../components/TrackItem";
import { fetchTracks } from "../api/track";
import { CreateTrackModal } from "../components/CreateTrackModal";

export const TrackPage = () => {
  const [tracks, setTracks] = useState<Track[]>([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchTracks()
      .then((res) => {
        setTracks(res.data);
      })
      .catch((err) => console.error("Помилка:", err));
  }, []);

  const handleTrackCreated = (track: Track) => {
    setTracks((prev) => [track, ...prev]); // додай новий трек до списку
  };

  return (
    <div className="p-4">
      <button
        onClick={() => setShowModal(true)}
        className="mb-4 bg-blue-500 text-white px-4 py-2 rounded"
      >
        Add track
      </button>

      <CreateTrackModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onTrackCreated={handleTrackCreated}
      />

      <h2 className="text-2xl font-semibold mb-4">Треки</h2>
      {tracks.map((track) => (
        <TrackItem key={track.id} track={track} />
      ))}
    </div>
  );
};
