import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  SelectChangeEvent,
  Button,
  IconButton,
} from "@mui/material";
import { Track } from "../../api/track/models/Track";
import FilterAltOutlinedIcon from "@mui/icons-material/FilterAltOutlined";
import React, { useState } from "react";

type Props = {
  tracks: Track[];
  onFilter: (filteredTracks: Track[]) => void;
};

export const TrackFilterBar: React.FC<Props> = ({ tracks, onFilter }) => {
  const [filters, setFilters] = useState("");
  const [genre, setGenres] = useState("");
  const [open, setOpen] = useState(false);

  const toggleOpen = () => setOpen((prev) => !prev);

  const handleFilterChange = () => {
    let filtered = [...tracks];

    if (genre) {
      filtered = filtered.filter((track) => track.genres.includes(genre));
    }

    if (filters === "titleAsc") {
      filtered.sort((a, b) => a.title.localeCompare(b.title));
    } else if (filters === "titleDesc") {
      filtered.sort((a, b) => b.title.localeCompare(a.title));
    } else if (filters === "artistAsc") {
      filtered.sort((a, b) => a.artist.localeCompare(b.artist));
    } else if (filters === "artistDesc") {
      filtered.sort((a, b) => b.artist.localeCompare(a.artist));
    }

    onFilter(filtered);
  };

  return (
    <Box
      sx={{
        display: "flex",
        gap: 2,
        mb: 2,
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <IconButton onClick={toggleOpen} color="inherit">
        <FilterAltOutlinedIcon />
      </IconButton>
      <FormControl sx={{ minWidth: 120 }}>
        <InputLabel>Genre</InputLabel>
        <Select
          value={genre}
          label="Genre"
          onChange={(e: SelectChangeEvent) => setGenres(e.target.value)}
        >
          <MenuItem value="">All</MenuItem>
          <MenuItem value="Pop">Pop</MenuItem>
          <MenuItem value="Rock">Rock</MenuItem>
          <MenuItem value="Jazz">Jazz</MenuItem>
          <MenuItem value="Hip-Hop">Hip-Hop</MenuItem>
        </Select>
      </FormControl>

      <FormControl sx={{ minWidth: 150 }}>
        <InputLabel>Sort By</InputLabel>
        <Select
          value={filters}
          label="Sort By"
          onChange={(e: SelectChangeEvent) => setFilters(e.target.value)}
        >
          <MenuItem value="">Default</MenuItem>
          <MenuItem value="titleAsc">Title A-Z</MenuItem>
          <MenuItem value="titleDesc">Title Z-A</MenuItem>
          <MenuItem value="artistAsc">Artist A-Z</MenuItem>
          <MenuItem value="artistDesc">Artist Z-A</MenuItem>
        </Select>
      </FormControl>

      <Button variant="contained" onClick={handleFilterChange}>
        Apply
      </Button>
    </Box>
  );
};
