import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { deleteTrack, fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";
import { TrackList } from "../components/TrackList/TrackList";
import { Track } from "../api/track/models/Track";
import { GetModel } from "../api/track/models/CreateTrack";
import { Box, Typography, Button, Paper } from "@mui/material";
import { SearchAppBar } from "../components/SeachBar/SearchBar";
import TablePagination from "@mui/material/TablePagination";
import LibraryMusicIcon from "@mui/icons-material/LibraryMusic";
import { TrackFilterBar } from "../components/TrackFilterSortBar/TrackFilterBar";

const model: GetModel = {
  page: 1,
  limit: 100,
  order: "",
  sort: "",
  genre: "",
  artist: "",
  search: "",
};

export const HomePage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks(model);

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      setTracks((prev) =>
        prev
          ? { ...prev, data: prev.data.filter((track) => track.id !== trackId) }
          : prev
      );
    } catch (error) {
      console.error(error);
    }
  };

  const handleTrackUpdated = (updatedTrack: Track) => {
    setTracks((prev) =>
      prev
        ? {
            ...prev,
            data: prev.data.map((t) =>
              t.id === updatedTrack.id ? updatedTrack : t
            ),
          }
        : prev
    );
  };
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const handleChangePage = (
    event: React.MouseEvent<HTMLButtonElement> | null,
    newPage: number
  ) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks({
        ...model,
        page: page + 1,
        limit: rowsPerPage,
      });

      if (tracks) {
        setTracks(tracks);
      }
    };
    fetch();
  }, [page, rowsPerPage]);

  return (
    <Box
      sx={{
        minHeight: "100vh",
        background: "linear-gradient(to bottom, #1c1f2e, #2c2f45)",
        padding: "40px 30px",
        fontFamily: "'Roboto', sans-serif",
        color: "#fff",
      }}
    >
      {/* Header */}
      <Paper
        elevation={5}
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "25px 35px",
          borderRadius: "30px",
          background: "linear-gradient(135deg, #5c6bc0, #8e99f3)",
          mb: 6,
          boxShadow: "0 10px 30px rgba(0,0,0,0.2)",
          color: "#fff",
        }}
      >
        <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
          <LibraryMusicIcon sx={{ fontSize: "50px", color: "#fff" }} />
          <Typography
            variant="h4"
            sx={{ fontWeight: 700, letterSpacing: "1px" }}
          >
            Track Manager
          </Typography>
        </Box>
        <Button
          variant="contained"
          onClick={() => setShowModal(true)}
          sx={{
            background: "linear-gradient(135deg, #ff6f61, #ff8a65)",
            fontWeight: "bold",
            borderRadius: "30px",
            px: 3,
            py: 1.2,
            textTransform: "none",
            boxShadow: "0 5px 15px rgba(0,0,0,0.2)",
            "&:hover": {
              background: "linear-gradient(135deg, #ff3d00, #ff7043)",
              transform: "translateY(-2px)",
            },
            transition: "0.3s",
          }}
        >
          + Add Track
        </Button>
      </Paper>

      {/* Search and Filter */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          width: "80%",
          maxWidth: 950,
          margin: "0 auto 40px auto",
          padding: "12px 20px",
          backgroundColor: "rgba(255,255,255,0.05)",
          borderRadius: 30,
          boxShadow: "0 6px 20px rgba(0,0,0,0.15)",
          gap: 3,
          backdropFilter: "blur(8px)",
        }}
      >
        {/* Search */}
        <Box
          sx={{
            flex: 1,
            display: "flex",
            alignItems: "center",
            backgroundColor: "rgba(255,255,255,0.1)",
            borderRadius: 25,
            px: 2,
            py: 0.5,
            input: {
              padding: "10px 14px",
              borderRadius: 25,
              backgroundColor: "transparent",
              fontSize: "16px",
              color: "#fff",
              "&::placeholder": { color: "#ccc" },
            },
          }}
        >
          <SearchAppBar
            onSearch={(response) => {
              setTracks(response);
              setPage(0);
            }}
          />
        </Box>

        {/* Filter */}
        <Box
          sx={{
            minWidth: 180,
            borderRadius: 25,
            backgroundColor: "rgba(255,255,255,0.1)",
            padding: "8px 16px",
            boxShadow: "0 3px 8px rgba(0,0,0,0.1)",
            cursor: "pointer",
            transition: "0.3s",
            "&:hover": {
              backgroundColor: "rgba(255,255,255,0.15)",
              boxShadow: "0 5px 12px rgba(0,0,0,0.15)",
            },
          }}
        >
          <TrackFilterBar
            tracks={tracks?.data || []}
            onFilter={(filteredTracks) => {
              if (tracks) {
                setTracks({
                  ...tracks,
                  data: filteredTracks,
                  meta: { ...tracks.meta, total: filteredTracks.length },
                });
              }
            }}
          />
        </Box>
      </Box>

      {/* Modal */}
      <CreateTrackModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onTrackCreated={handleTrackCreated}
      />

      {/* Track List */}
      <Paper
        elevation={4}
        sx={{
          borderRadius: "30px",
          p: 4,
          background: "rgba(255,255,255,0.05)",
          mb: 6,
          boxShadow: "0 8px 25px rgba(0,0,0,0.2)",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          backdropFilter: "blur(10px)",
        }}
      >
        {tracks?.data.length === 0 ? (
          <Typography
            variant="h6"
            align="center"
            sx={{ color: "#ccc", fontStyle: "italic", mt: 5 }}
          >
            Немає треків 💤
          </Typography>
        ) : (
          <TrackList
            tracks={tracks}
            onTrackDelete={handleTrackDelete}
            onTrackUpdated={handleTrackUpdated}
          />
        )}
      </Paper>

      {/* Pagination */}
      <Box sx={{ display: "flex", justifyContent: "center", mt: 3 }}>
        <TablePagination
          component="div"
          count={tracks?.meta.total || 0}
          page={page}
          onPageChange={handleChangePage}
          rowsPerPage={rowsPerPage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          sx={{
            background: "rgba(255,255,255,0.1)",
            borderRadius: "25px",
            px: 2,
            boxShadow: "0 4px 15px rgba(0,0,0,0.15)",
            "& .MuiTablePagination-toolbar": { minHeight: 60, color: "#fff" },
            color: "#fff",
          }}
        />
      </Box>
    </Box>
  );
};
