import React from "react";
import { Track } from "../api/track/models/Track";
import {
  Box,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  Typography,
} from "@mui/material";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import DeleteIcon from "@mui/icons-material/Delete";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
}

export const TrackListItem: React.FC<TrackItemProps> = ({
  track,
  onDelete,
}) => {
  return (
    <Card
      sx={{
        display: "flex",
        alignItems: "center",
        width: "100%",
        padding: 1,
        borderRadius: 2,
        boxShadow: 1,
        backgroundColor: "background.paper",
        "&:hover": {
          boxShadow: 4,
          backgroundColor: "action.hover",
        },
        transition: "0.3s",
      }}
    >
      {/* Обкладинка */}
      <CardMedia
        component="img"
        image={track.coverImage || "https://via.placeholder.com/50"}
        alt={track.title}
        sx={{
          width: 50,
          height: 50,
          borderRadius: 1,
          flexShrink: 0,
        }}
      />

      {/* Інформація про трек */}
      <CardContent sx={{ flexGrow: 1, ml: 2, py: 0 }}>
        <Typography
          variant="subtitle1"
          sx={{
            fontWeight: 500,
            overflow: "hidden",
            textOverflow: "ellipsis",
            whiteSpace: "nowrap",
          }}
        >
          {track.title}
        </Typography>
        <Typography
          variant="body2"
          color="text.secondary"
          sx={{
            overflow: "hidden",
            textOverflow: "ellipsis",
            whiteSpace: "nowrap",
          }}
        >
          {track.artist}
        </Typography>
      </CardContent>

      {/* Кнопки */}
      <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
        {track.audioFile && (
          <audio controls style={{ width: 150, borderRadius: 8 }}>
            <source src={track.audioFile} type="audio/mpeg" />
            Your browser does not support audio.
          </audio>
        )}
        <IconButton color="primary" aria-label="play">
          <PlayArrowIcon />
        </IconButton>
        <IconButton color="error" onClick={() => onDelete(track.id)}>
          <DeleteIcon />
        </IconButton>
      </Box>
    </Card>
  );
};
