import React, { useEffect, useState } from "react";
import { Track } from "../../api/track/models/Track";
import { useTheme } from "@mui/material/styles";
import {
  Box,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  Typography,
  Menu,
  MenuItem,
  Slider,
  Stack,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { useAudioPlayer } from "../../hooks/useAudioPlayer";
import { PlayTrack } from "../PlayTrack";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import { EditTrackModal } from "../EditTrackModal";
import { UploadTrackFile } from "../UploadTrackFile";
import VolumeUpRounded from "@mui/icons-material/VolumeUpRounded";
import VolumeDownRounded from "@mui/icons-material/VolumeDownRounded";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackListItem: React.FC<TrackItemProps> = ({
  track,
  onDelete,
  onTrackUpdated,
}) => {
  const theme = useTheme();
  const player = useAudioPlayer();

  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const openMenu = Boolean(anchorEl);
  const [isEditOpen, setIsEditOpen] = useState(false);

  const [duration, setDuration] = useState(0);
  const [position, setPosition] = useState(0);
  const [volume, setVolume] = useState(0.5);

  useEffect(() => {
    const audio = player.audioRef.current;
    if (!audio) return;

    const updateTime = () => setPosition(audio.currentTime);
    const setMeta = () => setDuration(audio.duration);

    audio.addEventListener("timeupdate", updateTime);
    audio.addEventListener("loadedmetadata", setMeta);

    return () => {
      audio.removeEventListener("timeupdate", updateTime);
      audio.removeEventListener("loadedmetadata", setMeta);
    };
  }, [player.audioRef]);

  const handleMenuClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleEditClick = () => {
    setIsEditOpen(true);
    handleMenuClose();
  };

  const formatDuration = (value: number) => {
    const minute = Math.floor(value / 60);
    const second = Math.floor(value % 60);
    return `${minute}:${second < 10 ? `0${second}` : second}`;
  };

  return (
    <Card
      sx={{
        display: "flex",
        flexDirection: "column",
        width: "100%",
        px: 3,
        py: 2,
        mb: 2,
        borderRadius: 3,
        boxShadow: 2,
        background: "linear-gradient(120deg, #ffe6f0 0%, #fff0f5 100%)",
      }}
    >
      {/* Верхня частина з інфою */}
      <Box sx={{ display: "flex", alignItems: "center" }}>
        <CardMedia
          component="img"
          sx={{
            width: 60,
            height: 60,
            borderRadius: 2,
            objectFit: "cover",
            boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
          }}
          image={
            track.coverImage ||
            "https://media.istockphoto.com/id/2167996762/uk/%D1%84%D0%BE%D1%82%D0%BE/flamingos-on-lake.jpg?s=2048x2048&w=is&k=20&c=A_twH4lS0xkDGqh1oSO9aOEREPNEJpdRUkwOwsXN7nE="
          }
          alt={track.title}
        />
        <Box sx={{ flexGrow: 1, ml: 2, minWidth: 0 }}>
          <CardContent sx={{ py: 0 }}>
            <Typography
              variant="subtitle1"
              sx={{
                fontWeight: 600,
                color: "#d81b60",
                whiteSpace: "nowrap",
                overflow: "hidden",
                textOverflow: "ellipsis",
              }}
            >
              {track.title}
            </Typography>
            <Typography
              variant="body2"
              sx={{
                color: "#8e24aa",
                whiteSpace: "nowrap",
                overflow: "hidden",
                textOverflow: "ellipsis",
              }}
            >
              {track.artist}
            </Typography>
            <Typography
              variant="body2"
              sx={{ color: "#6a1b9a", fontStyle: "italic" }}
            >
              {track.album || "Unknown album"}
            </Typography>
          </CardContent>
        </Box>

        {/* Кнопки */}
        <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
          <IconButton color="error" onClick={() => onDelete(track.id)}>
            <DeleteIcon />
          </IconButton>
          <IconButton onClick={handleMenuClick}>
            <MoreVertIcon />
          </IconButton>
        </Box>
      </Box>

      {/* Слайдер програвання */}
      {track.audioFile && (
        <Box sx={{ mt: 2 }}>
          <Slider
            size="small"
            value={position}
            min={0}
            step={1}
            max={duration || 0}
            onChange={(_, value) => {
              setPosition(value as number);
              if (player.audioRef.current) {
                player.audioRef.current.currentTime = value as number;
              }
            }}
          />
          <Box
            sx={{ display: "flex", justifyContent: "space-between", mt: -1 }}
          >
            <Typography variant="caption">
              {formatDuration(position)}
            </Typography>
            <Typography variant="caption">
              {formatDuration(duration - position)}
            </Typography>
          </Box>

          {/* Гучність */}
          <Stack direction="row" spacing={2} alignItems="center" sx={{ mt: 1 }}>
            <VolumeDownRounded />
            <Slider
              value={volume * 100}
              onChange={(_, value) => {
                const vol = (value as number) / 100;
                setVolume(vol);
                if (player.audioRef.current) {
                  player.audioRef.current.volume = vol;
                }
              }}
            />
            <VolumeUpRounded />
          </Stack>
        </Box>
      )}

      {/* Меню */}
      <Menu anchorEl={anchorEl} open={openMenu} onClose={handleMenuClose}>
        <MenuItem onClick={handleEditClick}>Edit Track</MenuItem>
        <MenuItem>
          <UploadTrackFile
            trackId={track.id}
            existingFileUrl={track.audioFile || ""}
            onFileUploaded={(url) =>
              onTrackUpdated({ ...track, audioFile: url })
            }
            onFileRemoved={() => onTrackUpdated({ ...track, audioFile: "" })}
          />
        </MenuItem>
      </Menu>

      <EditTrackModal
        isOpen={isEditOpen}
        onClose={() => setIsEditOpen(false)}
        track={track}
        onTrackUpdated={onTrackUpdated}
      />
    </Card>
  );
};
