import React from "react";
import { Track } from "../types/commonTypes";

interface TrackItemProps {
  track: Track;
}

export const TrackItem: React.FC<TrackItemProps> = ({ track }) => {
  return (
    <div className="bg-white rounded-xl shadow-md hover:shadow-xl transition duration-300 overflow-hidden border border-gray-200">
      {track.coverImage && (
        <img
          src={track.coverImage}
          alt={track.title}
          className="w-full h-48 object-cover"
        />
      )}

      <div className="p-4">
        <h3 className="text-lg font-bold text-gray-900 truncate">
          {track.title}
        </h3>
        <p className="text-sm text-gray-600 mb-2">{track.artist}</p>

        <div className="flex flex-wrap gap-2 mb-3">
          {track.genres?.map((genre) => (
            <span
              key={genre.id}
              className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded-full"
            >
              {genre.name}
            </span>
          ))}
        </div>

        {track.audioFile && (
          <audio controls className="w-full">
            <source src={track.audioFile} type="audio/mpeg" />
            Ваш браузер не підтримує аудіо.
          </audio>
        )}
      </div>
    </div>
  );
};
