import React from "react";
import { Track } from "../../api/track/models/Track";
import { TrackItem } from "../TrackItem";
import { deleteTrack } from "../../api/track/trackApi";

interface TrackTableProps {
  tracks: Track[];
  setTracks: (traks: Track[]) => void;
}

export const TrackTable: React.FC<TrackTableProps> = ({
  tracks,
  setTracks,
}) => {
  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      if (tracks) {
        setTracks({
          ...tracks,
          data: tracks.data.filter((track) => track.id !== trackId),
        });
      }
    } catch (error) {
      console.log(error);
      alert("Failed to delete track");
    }
  };
  return (
    <div>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <th>#</th>
          <th>Title</th>
          <th>Artist</th>
          <th>Album</th>
          <th>Date added</th>
          <th>Duration</th>
        </thead>
        <tbody>
          {tracks.map((track, index) => (
            <tr key={track.id} style={{ borderBottom: "1px solid #ddd" }}>
              <td>{index + 1}</td>
              <td>{track.title}</td>
              <td>{track.artist}</td>
              <td>{track.album || "-"}</td>
              <td>
                {track.createdAt
                  ? new Date(track.createdAt).toLocaleDateString()
                  : "-"}
              </td>
              <td>{track.audioFile ? "üîä" : "-"}</td>
              <td>
                <button onClick={() => onTrackDelete(track.id)}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
