import React from "react";
import { Track } from "../api/track/models/Track";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
}

export const TrackItem: React.FC<TrackItemProps> = ({ track, onDelete }) => {
  return (
    <div className="bg-white rounded-2xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300 border border-gray-200 hover:-translate-y-1 transform">
      {/* Cover Image */}
      {track.coverImage && (
        <div className="relative group">
          <img
            src={
              "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pexels.com%2Fsearch%2Fbeautiful%2F&psig=AOvVaw3iw-yAhiqHh6_FNEqbS1uR&ust=1755772472316000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCIDKr62YmY8DFQAAAAAdAAAAABAE"
            }
            alt={track.title}
            className="w-full h-52 object-cover group-hover:scale-105 transition-transform duration-300"
          />
          <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/70 to-transparent p-3">
            <h3 className="text-white text-lg font-semibold truncate">
              {track.title}
            </h3>
          </div>
        </div>
      )}

      {/* Content */}
      <div className="p-4">
        <p className="text-gray-800 font-semibold">{track.artist}</p>
        <p className="text-sm text-gray-500">{track.album}</p>

        {/* Audio Player */}
        {track.audioFile && (
          <audio
            controls
            className="w-full mt-4 rounded-lg border border-gray-300"
          >
            <source src={track.audioFile} type="audio/mpeg" />
            Ваш браузер не підтримує аудіо.
          </audio>
        )}

        <button
          className="mt-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition"
          onClick={() => onDelete(track.id)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};
