import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { TrackItem } from "../components/TrackItem";
import { deleteTrack, fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";
import { TrackList } from "../components/TrackList";
import { Track } from "../api/track/models/Track";
import { Button } from "@mui/material";
import { data } from "react-router-dom";
import { GetModel } from "../api/track/models/CreateTrack";
import { Preview } from "@mui/icons-material";

const model: GetModel = {
  page: 1,
  limit: 20,
  order: "",
  sort: "",
  genre: "",
  artist: "",
  search: "",
};

export const TrackPage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks(model);

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      setTracks((prev) =>
        prev
          ? { ...prev, data: prev.data.filter((track) => track.id !== trackId) }
          : prev
      );
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-tr from-indigo-50 via-purple-50 to-pink-50 py-12 px-6 sm:px-12">
      <div className="max-w-6xl mx-auto bg-white shadow-2xl rounded-3xl p-10 sm:p-14">
        <header className="flex flex-col sm:flex-row justify-between items-center mb-10 gap-4 sm:gap-0">
          <h1 className="text-4xl sm:text-5xl font-extrabold text-indigo-700 flex items-center gap-3 select-none">
            üéß Tracks
          </h1>
        </header>

        <Button
          variant="contained"
          onClick={() => setShowModal(true)}
          sx={{ backgroundColor: "#1976d2", color: "#fff", fontWeight: "bold" }}
        >
          ‚ûï Create Track
        </Button>

        <CreateTrackModal
          isOpen={showModal}
          onClose={() => setShowModal(false)}
          onTrackCreated={handleTrackCreated}
        />

        {tracks?.data.length === 0 ? (
          <p className="text-center text-gray-400 italic text-lg mt-20 select-none">
            –ù–µ–º–∞—î —Ç—Ä–µ–∫—ñ–≤ üí§
          </p>
        ) : (
          <TrackList tracks={tracks} onTrackDelete={handleTrackDelete} />
        )}
      </div>
    </div>
  );
};
