import { useEffect, useState } from "react";
import { Track } from "../types/track";
import { TrackItem } from "../components/TrackItem";
import { fetchTracks } from "../api/track";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";

export const TrackPage = () => {
  const [tracks, setTracks] = useState<Track[]>([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchTracks()
      .then((res) => {
        setTracks(res.data);
      })
      .catch((err) => console.error("–ü–æ–º–∏–ª–∫–∞:", err));
  }, []);

  const handleTrackCreated = (track: Track) => {
    setTracks((prev) => [track, ...prev]); // –¥–æ–¥–∞–π –Ω–æ–≤–∏–π —Ç—Ä–µ–∫ –¥–æ —Å–ø–∏—Å–∫—É
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded-2xl p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">üéß –¢—Ä–µ–∫–∏</h2>
          <button
            onClick={() => setShowModal(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            ‚ûï –î–æ–¥–∞—Ç–∏ —Ç—Ä–µ–∫
          </button>
        </div>

        <CreateTrackModal
          isOpen={showModal}
          onClose={() => setShowModal(false)}
          onTrackCreated={handleTrackCreated}
        />

        <div className="space-y-4">
          {tracks.length === 0 ? (
            <p className="text-gray-500">–ù–µ–º–∞—î —Ç—Ä–µ–∫—ñ–≤</p>
          ) : (
            tracks.map((track) => <TrackItem key={track.id} track={track} />)
          )}
        </div>
      </div>
    </div>
  );
};
