import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { TrackItem } from "../components/TrackItem";
import { deleteTrack, fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";
import { TrackList } from "../components/TrackList/TrackList";
import { Track } from "../api/track/models/Track";
import { Button } from "@mui/material";
import { data } from "react-router-dom";
import { GetModel } from "../api/track/models/CreateTrack";
import { Box, Typography } from "@mui/material";
import SearchBar from "../components/SearchBar";

const model: GetModel = {
  page: 1,
  limit: 50,
  order: "",
  sort: "",
  genre: "",
  artist: "",
  search: "",
};

export const TrackPage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks(model);

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      setTracks((prev) =>
        prev
          ? { ...prev, data: prev.data.filter((track) => track.id !== trackId) }
          : prev
      );
    } catch (error) {
      console.error(error);
    }
  };

  const [currentTrack, setCurrentTrack] = useState<Track | null>(null);

  return (
    <Box
      sx={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        padding: "20px",
      }}
    >
      <Typography
        sx={{
          fontWeight: "800",
          color: "#3E2723",
          textAlign: "center",
          fontSize: "2.7rem",
          backgroundColor: "#F4C9D6",
          borderRadius: "50px",
          width: "300px",
          padding: "10px",
        }}
      >
        Track Manager
      </Typography>
      <Box
        sx={{
          display: "flex",
          justifyContent: "flex-end",
          width: "100%",
          marginTop: "10px",
        }}
      >
        <Button
          variant="contained"
          onClick={() => setShowModal(true)}
          sx={{
            backgroundColor: "#F4C9D6",
            color: "#3E2723",
            fontWeight: "bold",
          }}
        >
          Create Track
        </Button>
      </Box>

      <Box
        sx={{
          display: "flex",
          alignItems: "flex-end",
          width: "100%",
          marginTop: "20px",
        }}
      >
        <Box sx={{ width: "75%", marginRight: "30px" }}>
          <SearchBar />
        </Box>
      </Box>

      <CreateTrackModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onTrackCreated={handleTrackCreated}
      />

      {tracks?.data.length === 0 ? (
        <p className="text-center text-gray-400 italic text-lg mt-20 select-none">
          –ù–µ–º–∞—î —Ç—Ä–µ–∫—ñ–≤ üí§
        </p>
      ) : (
        <>
          <TrackList
            tracks={tracks}
            onTrackDelete={handleTrackDelete}
            onPlay={setCurrentTrack}
          />

          {currentTrack && (
            <audio
              controls
              autoPlay
              src={`http://localhost:8000${currentTrack.audioFile}`}
              style={{ marginTop: "20px", width: "100%" }}
            />
          )}
        </>
      )}
    </Box>
  );
};
