import React from "react";
import { TrackPage } from "../pages/TracksPage";
import { deleteTrack } from "../api/track/trackApi";

export const handleTrackDelete = async (trackId: string) => {
  try {
    await deleteTrack({ id: trackId });
    setTracks((prev) =>
      prev
        ? {
            ...prev,
            data: prev.data.filter(
              (track: { id: string }) => track.id !== trackId
            ),
          }
        : prev
    );
  } catch (error) {
    console.error(error);
  }
};

function setTracks(arg0: (prev: any) => any) {
  throw new Error("Function not implemented.");
}
