// src/components/CreateTrackModal.tsx

import Modal from "../Modal";
import { TrackForm } from "../TrackForm";
import { Track } from "../../types/track";
import { createTrack } from "../../api/track"; // Функція POST-запиту

type Props = {
  isOpen: boolean;
  onClose: () => void;
  onTrackCreated?: (track: Track) => void; // щоб додати новий трек до списку
};

export const CreateTrackModal = ({
  isOpen,
  onClose,
  onTrackCreated,
}: Props) => {
  const handleSave = async (trackData: Partial<Track>) => {
    try {
      const newTrack = await createTrack(trackData);
      onTrackCreated?.(newTrack); // оновлюємо список на сторінці
      onClose(); // закриваємо модалку
    } catch (err) {
      console.error("Помилка створення треку:", err);
      alert("Не вдалося створити трек");
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose}>
      <h2 className="text-xl font-bold mb-4">Створити новий трек</h2>
      <TrackForm onSave={handleSave} />
    </Modal>
  );
};
