import { useEffect, useState } from "react";
import { Genre } from "../types/track";

export function useGenres(){
    const [genres, setGenres] = useState<Genre[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() =>{
        fetch("/api/genres")
        .then(res=>res.json())
        .then(data =>{
            setGenres(data);
            setLoading(false);
        });
    },[]);

    return {genres,loading};
}
