import React from "react";
import { Track } from "../../api/track/models/Track";
import { Box, Typography, List, ListItem, Divider, Paper } from "@mui/material";
import { PageList } from "../../types/PageList";
import { TrackListItem } from "./TrackListItem";

interface Props {
  tracks: PageList<Track> | undefined;
  onTrackDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackList: React.FC<Props> = ({
  tracks,
  onTrackDelete,
  onTrackUpdated,
}) => {
  return (
    <Box
      sx={{
        width: "100%",
        p: { xs: 2, sm: 3, md: 4 }, // –∞–¥–∞–ø—Ç–∏–≤–Ω—ñ –≤—ñ–¥—Å—Ç—É–ø–∏
      }}
    >
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          fontWeight: "bold",
          textAlign: "center",
          mb: 4,
          color: "#4A148C",
          letterSpacing: 1,
          textTransform: "uppercase",
        }}
      >
        üé∂ List of Tracks
      </Typography>

      {/* –°–ø–∏—Å–æ–∫ */}
      <List disablePadding>
        {tracks?.data.map((track, index) => (
          <React.Fragment key={track.id}>
            <ListItem
              sx={{
                pl: { xs: 2, sm: 3, md: 5 }, // —Ç—ñ–ª—å–∫–∏ –∑–ª—ñ–≤–∞
                pr: { xs: 1, sm: 2, md: 3 }, // —Å–ø—Ä–∞–≤–∞ –º–µ–Ω—à–µ
                py: 2.5,
                borderRadius: 3,
                mb: 2,
                background: "linear-gradient(135deg, #fafafa 0%, #fce4ec 100%)",
                boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
                transition: "all 0.3s ease",
                "&:hover": {
                  background:
                    "linear-gradient(135deg, #f8bbd0 0%, #e1bee7 100%)",
                  transform: "scale(1.01)",
                  boxShadow: "0 6px 20px rgba(0,0,0,0.12)",
                },
              }}
            >
              <TrackListItem
                track={track}
                onDelete={onTrackDelete}
                onTrackUpdated={onTrackUpdated}
              />
            </ListItem>

            {index < (tracks?.data.length ?? 0) - 1 && (
              <Divider
                sx={{
                  borderColor: "rgba(0,0,0,0.05)",
                  my: 1,
                }}
              />
            )}
          </React.Fragment>
        ))}
      </List>
    </Box>
  );
};
