import React from "react";
import { Track } from "../../api/track/models/Track";
import { Box, Typography, List, ListItem, Divider, Paper } from "@mui/material";
import { PageList } from "../../types/PageList";
import { TrackListItem } from "./TrackListItem";

interface Props {
  tracks: PageList<Track> | undefined;
  onTrackDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackList: React.FC<Props> = ({
  tracks,
  onTrackDelete,
  onTrackUpdated,
}) => {
  return (
    <Box
      sx={{
        width: "100%",
        maxWidth: 1000,
        margin: "0 auto",
        p: 3,
        border: 4,
        borderColor: "red",
      }}
    >
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <Typography
        variant="h4"
        gutterBottom
        sx={{
          fontWeight: "bold",
          textAlign: "center",
          mb: 4,
          color: "#4A148C",
          letterSpacing: 1,
          textTransform: "uppercase",
        }}
      >
        üé∂ List of Tracks
      </Typography>

      {/* –°–ø–∏—Å–æ–∫ */}
      <Paper
        elevation={0}
        sx={{
          borderRadius: 4,
          background: "linear-gradient(135deg, #fafafa 0%, #fce4ec 100%)",
          p: 2,
        }}
      >
        <List disablePadding>
          {tracks?.data.map((track, index) => (
            <React.Fragment key={track.id}>
              <ListItem
                sx={{
                  width: "100%",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  px: 3,
                  py: 2.5,
                  mb: 2,
                  borderRadius: 3,
                  backgroundColor: "#fff",
                  boxShadow: "0 4px 12px rgba(0,0,0,0.06)",
                  transition: "all 0.3s ease",
                  "&:hover": {
                    background:
                      "linear-gradient(135deg, #f8bbd0 0%, #e1bee7 100%)",
                    transform: "translateY(-3px) scale(1.01)",
                    boxShadow: "0 6px 18px rgba(0,0,0,0.12)",
                  },
                }}
              >
                <TrackListItem
                  track={track}
                  onDelete={onTrackDelete}
                  onTrackUpdated={onTrackUpdated}
                />
              </ListItem>
            </React.Fragment>
          ))}
        </List>
      </Paper>
    </Box>
  );
};
