import * as React from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { Box, Step, StepLabel, Stepper } from "@mui/material";
import { Chip } from "@mui/material";
import { useState } from "react";
import { url } from "inspector";
import { createTrack } from "../../api/track/trackApi";
import { Track } from "../../api/track/models/Track";
import { UploadTrackFile } from "../UploadTrtackFile/UploadTrackFile";

interface CreateTrackModalProps {
  isOpen: boolean;
  onClose: () => void;
  onTrackCreated: (track: Track) => void;
}

export const CreateTrackModal: React.FC<CreateTrackModalProps> = ({
  isOpen,
  onClose,
  onTrackCreated,
}) => {
  const [title, setTitle] = useState("");
  const [artist, setArtist] = useState("");
  const [album, setAlbum] = useState("");
  const [genres, setGenres] = useState<string[]>([]);
  const [coverImage, setCoverImage] = useState("");
  const [newGenre, setNewGenre] = useState("");
  const [createdTrack, setCreatedTrack] = useState<Track | null>(null);
  const [currentStep, setCurrentStep] = useState(0);

  const steps = ["Information about track", "Downloading track"];

  const handleAddGenre = () => {
    const trimmedGenre = newGenre.trim();
    if (newGenre.trim() && !genres.includes(trimmedGenre)) {
      setGenres([...genres, trimmedGenre]);
      setNewGenre("");
    }
  };

  const handleRemoveGenre = (genreToRemove: string) => {
    setGenres(genres.filter((g) => g !== genreToRemove));
  };

  const validateImageUrl = (url: string) => {
    return /^https?:\/\/.*\.(jpg|jpeg|png|gif|webp)$/i.test(url);
  };

  const handleClose = () => {
    setTitle("");
    setArtist("");
    setAlbum("");
    setGenres([]);
    setCoverImage("");
    setNewGenre("");
    setCreatedTrack(null);
    setCurrentStep(0);
    onClose();
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    if (!title.trim() || !artist.trim()) {
      alert("Track title and artist are required");
      return;
    }

    if (!coverImage) {
      alert("Invalid image link format");
      return;
    }

    const trackData = {
      title,
      artist,
      album,
      coverImage: coverImage,
      genres,
    };

    try {
      const savedTrack = await createTrack(trackData);
      if (savedTrack) {
        setCreatedTrack(savedTrack);
        onTrackCreated(savedTrack);
        // handleClose();
      } else {
        console.log("Track was not saved");
      }
    } catch (error) {
      console.log("Error creating track:", error);
    }
  };

  const handleFileUploaded = (url: string) => {
    if (createdTrack) {
      const updatedTrack = { ...createdTrack, audioFile: url };
      setCreatedTrack(updatedTrack);
      onTrackCreated(updatedTrack);
    }
  };

  const handleFinish = () => {
    if (createdTrack) {
      if (createdTrack.audioFile) {
        handleClose();
      } else {
        const confirmSkip = window.confirm(
          "Ви впевнені, що хочете завершити без завантаження музичного файлу?"
        );
        if (confirmSkip) {
          handleClose();
        }
      }
    }
  };

  const handleSkipUpload = () => {
    if (createdTrack) {
      onTrackCreated(createdTrack);
      handleClose();
    }
  };
  return (
    <>
      <Dialog
        open={isOpen}
        onClose={handleClose}
        fullWidth
        maxWidth="sm"
        PaperProps={{
          sx: {
            borderRadius: 3,
            padding: 2,
            backgroundColor: "#f5f5f5",
          },
        }}
      >
        <DialogTitle
          sx={{ fontWeight: "bold", fontSize: "1.5rem", textAlign: "center" }}
        >
          Create a New Track
        </DialogTitle>

        <Box sx={{ px: 3, mb: 2 }}>
          <Stepper activeStep={currentStep} alternativeLabel>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        </Box>

        <DialogContent>
          {currentStep == 0 && (
            <Box
              component="form"
              onSubmit={handleSubmit}
              id="create-track-form"
              sx={{
                mt: 2,
                display: "flex",
                flexDirection: "column",
                gap: 2,
              }}
            >
              <TextField
                label="Track Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                fullWidth
                required
                variant="outlined"
              />
              <TextField
                label="Artist"
                value={artist}
                onChange={(e) => setArtist(e.target.value)}
                fullWidth
                required
                variant="outlined"
              />
              <TextField
                label="Album"
                value={album}
                onChange={(e) => setAlbum(e.target.value)}
                fullWidth
                variant="outlined"
              />
              <TextField
                label="Cover Image URL"
                value={coverImage}
                onChange={(e) => setCoverImage(e.target.value)}
                fullWidth
              />

              <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
                <TextField
                  label="Add Genre"
                  value={newGenre}
                  onChange={(e) => setNewGenre(e.target.value)}
                  size="small"
                  fullWidth
                />
                <Button
                  onClick={handleAddGenre}
                  variant="contained"
                  sx={{
                    height: "40px",
                    minWidth: "40px",
                    backgroundColor: "#4caf50",
                  }}
                >
                  +
                </Button>
              </Box>

              <Box sx={{ display: "flex", flexWrap: "wrap", gap: 1 }}>
                {genres.map((genre) => (
                  <Chip
                    key={genre}
                    label={genre}
                    onDelete={() => handleRemoveGenre(genre)}
                    color="primary"
                    sx={{ fontWeight: "bold" }}
                  />
                ))}
              </Box>
            </Box>
          )}{" "}
          {currentStep == 1 && createdTrack && (
            <Box sx={{ display: "flex", gap: 1 }}>
              <Button
                onClick={handleSkipUpload}
                variant="outlined"
                sx={{ fontWeight: "bold" }}
              >
                Пропустити завантаження
              </Button>
              <Button
                onClick={handleFinish}
                variant="contained"
                sx={{
                  backgroundColor: createdTrack?.audioFile
                    ? "#4caf50"
                    : "#1976d2",
                  fontWeight: "bold",
                }}
              >
                {createdTrack?.audioFile ? "Завершити" : "Завершити без файлу"}
              </Button>
            </Box>
            // <Box>
            //   <UploadTrackFile
            //     trackId={createdTrack.id}
            //     existingFileUrl={createdTrack.audioFile || ""}
            //     onFileUploaded={(url) =>
            //       setCreatedTrack({ ...createdTrack, audioFile: url })
            //     }
            //   />
            // </Box>
          )}
        </DialogContent>

        <DialogActions
          sx={{ justifyContent: "space-between", paddingX: 3, paddingY: 1 }}
        >
          <Button
            onClick={handleClose}
            sx={{ color: "#f44336", fontWeight: "bold" }}
          >
            Cancel
          </Button>
          {!createdTrack && (
            <Button
              type="submit"
              form="create-track-form"
              variant="contained"
              sx={{ backgroundColor: "#1976d2", fontWeight: "bold" }}
            >
              Save
            </Button>
          )}
        </DialogActions>
      </Dialog>
    </>
  );
};
