import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { deleteTrack, fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";
import { TrackList } from "../components/TrackList/TrackList";
import { Track } from "../api/track/models/Track";
import { GetModel } from "../api/track/models/CreateTrack";
import { Box, Typography, Button, Paper } from "@mui/material";
import { SearchAppBar } from "../components/SeachBar/SearchBar";
import TablePagination from "@mui/material/TablePagination";
import LibraryMusicIcon from "@mui/icons-material/LibraryMusic";
import { TrackFilterBar } from "../components/TrackFilterSortBar/TrackFilterBar";

const model: GetModel = {
  page: 1,
  limit: 10,
  order: "",
  sort: "",
  genre: "",
  artist: "",
  search: "",
};

export const HomePage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks(model);

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      setTracks((prev) =>
        prev
          ? { ...prev, data: prev.data.filter((track) => track.id !== trackId) }
          : prev
      );
    } catch (error) {
      console.error(error);
    }
  };

  const handleTrackUpdated = (updatedTrack: Track) => {
    setTracks((prev) =>
      prev
        ? {
            ...prev,
            data: prev.data.map((t) =>
              t.id === updatedTrack.id ? updatedTrack : t
            ),
          }
        : prev
    );
  };
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const handleChangePage = (
    event: React.MouseEvent<HTMLButtonElement> | null,
    newPage: number
  ) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks({
        ...model,
        page: page + 1,
        limit: rowsPerPage,
      });

      if (tracks) {
        setTracks(tracks);
      }
    };
    fetch();
  }, [page, rowsPerPage]);

  return (
    <Box
      sx={{
        minHeight: "100vh",
        background:
          "linear-gradient(135deg, #fff5f7 0%, #ffe8f0 50%, #fff0f5 100%)",
        padding: "30px",
        fontFamily: "'Roboto', sans-serif",
        position: "relative",
        "&::before": {
          content: '""',
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          height: "300px",
          background:
            "linear-gradient(135deg, #ff758c 0%, #ff7eb3 50%, #ff758c 100%)",
          opacity: 0.1,
          borderRadius: "0 0 50% 50%",
          zIndex: 0,
        },
      }}
    >
      {/* Header */}
      <Paper
        elevation={0}
        sx={{
          position: "relative",
          zIndex: 1,
          display: "flex",
          flexDirection: { xs: "column", sm: "row" },
          alignItems: "center",
          justifyContent: "space-between",
          padding: { xs: "20px 24px", sm: "24px 36px" },
          borderRadius: "24px",
          background:
            "linear-gradient(135deg, #ff758c 0%, #ff7eb3 50%, #ff758c 100%)",
          mb: 5,
          boxShadow: "0 20px 60px rgba(255, 117, 140, 0.35)",
          border: "1px solid rgba(255, 255, 255, 0.3)",
          backdropFilter: "blur(10px)",
        }}
      >
        {/* Left Side: Icon + Title */}
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            gap: 2,
            justifyContent: "center",
          }}
        >
          <LibraryMusicIcon
            sx={{
              fontSize: { xs: 44, sm: 54 },
              color: "#ffffff",
              filter: "drop-shadow(0 4px 8px rgba(0,0,0,0.2))",
            }}
          />
          <Typography
            variant="h4"
            sx={{
              fontWeight: 800,
              color: "#ffffff",
              letterSpacing: "0.5px",
              fontSize: { xs: "1.7rem", sm: "2.2rem" },
              textShadow: "0 2px 12px rgba(0,0,0,0.15)",
            }}
          >
            Track Manager
          </Typography>
        </Box>

        {/* Right Side: Add Track Button */}
        <Button
          variant="contained"
          onClick={() => setShowModal(true)}
          sx={{
            background: "#ffffff",
            color: "#ff758c",
            fontWeight: "bold",
            borderRadius: "30px",
            px: { xs: 3, sm: 4 },
            py: { xs: 1.2, sm: 1.4 },
            textTransform: "none",
            fontSize: { xs: "0.95rem", sm: "1.05rem" },
            boxShadow: "0 8px 24px rgba(255, 255, 255, 0.4)",
            transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
            border: "2px solid rgba(255, 255, 255, 0.5)",
            "&:hover": {
              background: "#ffffff",
              transform: "translateY(-3px) scale(1.03)",
              boxShadow: "0 12px 32px rgba(255, 255, 255, 0.6)",
              color: "#ff7eb3",
            },
            mt: { xs: 2, sm: 0 },
          }}
        >
          + Add Track
        </Button>
      </Paper>

      {/* Search + Filter */}
      <Box
        sx={{
          position: "relative",
          zIndex: 1,
          display: "flex",
          flexDirection: { xs: "column", md: "row" },
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
          margin: "0 auto 32px",
          gap: { xs: 2, md: 3 },
        }}
      >
        {/* Search */}
        <Paper
          elevation={0}
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flex: { xs: "1 1 100%", md: "0 0 70%" },
            padding: { xs: "14px 20px", sm: "18px 28px" },
            borderRadius: "60px",
            background: "#ffffff",
            boxShadow: "0 8px 32px rgba(255, 117, 140, 0.15)",
            border: "2px solid rgba(255, 126, 179, 0.1)",
            transition: "all 0.3s ease",
            "&:hover": {
              boxShadow: "0 12px 40px rgba(255, 117, 140, 0.25)",
              borderColor: "rgba(255, 126, 179, 0.3)",
            },
            input: {
              width: "100%",
              padding: { xs: "10px 14px", sm: "12px 16px" },
              borderRadius: "20px",
              border: "none",
              background: "transparent",
              fontSize: "1rem",
              "&:focus": {
                outline: "none",
              },
              "&::placeholder": {
                color: "rgba(255, 117, 140, 0.5)",
              },
            },
          }}
        >
          <Box sx={{ width: "100%", maxWidth: 1200, my: "auto" }}>
            <SearchAppBar
              onSearch={(response) => {
                setTracks(response);
                setPage(0);
              }}
            />
          </Box>
        </Paper>

        {/* Filter */}
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
          }}
        >
          <Paper
            elevation={0}
            sx={{
              borderRadius: "50px",
              padding: "8px 12px",
              width: { xs: "100%", md: 140 },
              height: 48,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              background: "linear-gradient(135deg, #ff758c 0%, #ff7eb3 100%)",
              boxShadow: "0 6px 20px rgba(255, 117, 140, 0.3)",
              cursor: "pointer",
              transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
              border: "2px solid rgba(255, 255, 255, 0.3)",
              "&:hover": {
                boxShadow: "0 10px 30px rgba(255, 117, 140, 0.45)",
                transform: "translateY(-3px) scale(1.05)",
              },
            }}
          >
            <TrackFilterBar
              tracks={tracks?.data || []}
              onFilter={(filteredTracks) => {
                if (tracks) {
                  setTracks({
                    ...tracks,
                    data: filteredTracks,
                    meta: { ...tracks.meta, total: filteredTracks.length },
                  });
                }
              }}
            />
          </Paper>
        </Box>
      </Box>

      {/* Modal */}
      <CreateTrackModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onTrackCreated={handleTrackCreated}
      />

      {/* Track List */}
      <Box sx={{ position: "relative", zIndex: 1 }}>
        {tracks?.data.length === 0 ? (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              mt: 8,
              mb: 8,
            }}
          >
            <Typography
              variant="h5"
              sx={{
                color: "#ff7eb3",
                fontWeight: 600,
                mb: 1,
                background: "linear-gradient(135deg, #ff758c 0%, #ff7eb3 100%)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
              }}
            >
              Немає треків 💤
            </Typography>
            <Typography
              variant="body2"
              sx={{ color: "rgba(255, 117, 140, 0.6)", fontStyle: "italic" }}
            >
              Додайте свій перший трек!
            </Typography>
          </Box>
        ) : (
          <TrackList
            tracks={tracks}
            onTrackDelete={handleTrackDelete}
            onTrackUpdated={handleTrackUpdated}
          />
        )}
      </Box>

      {/* Pagination */}
      <Box
        sx={{
          position: "relative",
          zIndex: 1,
          display: "flex",
          justifyContent: "center",
          mt: 4,
        }}
      >
        <TablePagination
          component="div"
          count={tracks?.meta.total || 0}
          page={page}
          onPageChange={handleChangePage}
          rowsPerPage={rowsPerPage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          sx={{
            background: "#ffffff",
            borderRadius: "24px",
            px: 3,
            py: 1,
            boxShadow: "0 8px 32px rgba(255, 117, 140, 0.15)",
            border: "2px solid rgba(255, 126, 179, 0.1)",
            "& .MuiTablePagination-toolbar": {
              minHeight: 64,
            },
            "& .MuiTablePagination-selectLabel, & .MuiTablePagination-displayedRows":
              {
                color: "#ff758c",
                fontWeight: 500,
              },
            "& .MuiTablePagination-select": {
              color: "#ff7eb3",
              fontWeight: 600,
            },
            "& .MuiIconButton-root": {
              color: "#ff758c",
              transition: "all 0.3s ease",
              "&:hover": {
                background: "rgba(255, 117, 140, 0.1)",
                transform: "scale(1.1)",
              },
              "&.Mui-disabled": {
                color: "rgba(255, 117, 140, 0.3)",
              },
            },
          }}
        />
      </Box>
    </Box>
  );
};
