import React from "react";
import { useTheme } from "@mui/material/styles";
import { Box, IconButton, Slider, Typography, Stack } from "@mui/material";
import SkipPreviousIcon from "@mui/icons-material/SkipPrevious";
import PlayArrowRounded from "@mui/icons-material/PlayArrowRounded";
import PauseRounded from "@mui/icons-material/PauseRounded";
import SkipNextIcon from "@mui/icons-material/SkipNext";
import VolumeUpRounded from "@mui/icons-material/VolumeUpRounded";
import VolumeDownRounded from "@mui/icons-material/VolumeDownRounded";

import { useAudioPlayer } from "../hooks/useAudioPlayer";
import { Track } from "../api/track/models/Track";

interface PlayTrackProps {
  track: Track;
  player: ReturnType<typeof useAudioPlayer>;
}

export const PlayTrack: React.FC<PlayTrackProps> = ({ track, player }) => {
  const { audioRef, isPlaying, currentTrackId, togglePlay } = player;

  return (
    <Box sx={{ p: 2, borderRadius: 3, bgcolor: "rgba(255,255,255,0.4)" }}>
      <audio ref={audioRef} />

      <Box sx={{ display: "flex", alignItems: "center", mb: 2 }}>
        <img
          src={track.coverImage}
          alt={track.title}
          style={{ width: 80, height: 80, borderRadius: 8, marginRight: 12 }}
        />
        <Box>
          <Typography variant="subtitle1">{track.title}</Typography>
          <Typography variant="caption" color="text.secondary">
            {track.artist}
          </Typography>
        </Box>
      </Box>

      <Slider
        aria-label="time-indicator"
        size="small"
        value={0} // TODO: підключити currentTime
        min={0}
        step={1}
        max={200} // TODO: замінити на duration
        sx={{ mb: 1 }}
      />

      <Box sx={{ display: "flex", justifyContent: "center", gap: 2 }}>
        <IconButton aria-label="previous">
          <SkipPreviousIcon />
        </IconButton>
        <IconButton
          onClick={() =>
            track.audioFile &&
            togglePlay(
              track.id,
              `http://localhost:8000/api/files/${track.audioFile}`
            )
          }
        >
          {currentTrackId === track.id && isPlaying ? (
            <PauseRounded sx={{ fontSize: "2rem" }} />
          ) : (
            <PlayArrowRounded sx={{ fontSize: "2rem" }} />
          )}
        </IconButton>
        <IconButton aria-label="next">
          <SkipNextIcon />
        </IconButton>
      </Box>

      <Stack direction="row" alignItems="center" spacing={2} mt={2}>
        <VolumeDownRounded />
        <Slider aria-label="volume" defaultValue={30} />
        <VolumeUpRounded />
      </Stack>
    </Box>
  );
};
