import React from "react";
import { Track } from "../../api/track/models/Track";
import { Box, Typography, List, ListItem, Divider, Paper } from "@mui/material";
import { PageList } from "../../types/PageList";
import { TrackListItem } from "./TrackListItem";

interface Props {
  tracks: PageList<Track> | undefined;
  onTrackDelete: (trackId: string) => void;
  onTrackUpdated: (track: Track) => void;
}

export const TrackList: React.FC<Props> = ({
  tracks,
  onTrackDelete,
  onTrackUpdated,
}) => {
  return (
    <Box
      sx={{
        width: "100%",
        maxWidth: 1400,
        margin: "0 auto",
        p: { xs: 2, md: 5 },
        minHeight: "100vh",
        background: "linear-gradient(180deg, #fafbff 0%, #f0f4ff 100%)",
      }}
    >
      {/* Заголовок з анімацією */}
      <Box
        sx={{
          textAlign: "center",
          mb: 6,
          position: "relative",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            fontWeight: 800,
            background:
              "linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)",
            backgroundClip: "text",
            WebkitBackgroundClip: "text",
            WebkitTextFillColor: "transparent",
            letterSpacing: -1,
            fontSize: { xs: "2.5rem", md: "4rem" },
            mb: 1,
            animation: "fadeInDown 0.8s ease-out",
            "@keyframes fadeInDown": {
              from: {
                opacity: 0,
                transform: "translateY(-20px)",
              },
              to: {
                opacity: 1,
                transform: "translateY(0)",
              },
            },
          }}
        >
          Your Music Library
        </Typography>
        <Typography
          variant="subtitle1"
          sx={{
            color: "#64748b",
            fontSize: { xs: "0.9rem", md: "1.1rem" },
            fontWeight: 400,
          }}
        >
          {tracks?.data.length || 0} tracks in your collection
        </Typography>
      </Box>

      {/* Сітка треків */}
      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: { xs: "1fr", lg: "1fr 1fr" },
          gap: 3,
        }}
      >
        {tracks?.data.map((track, index) => (
          <Box
            key={track.id}
            sx={{
              animation: "fadeInUp 0.6s ease-out",
              animationDelay: `${index * 0.05}s`,
              animationFillMode: "both",
              "@keyframes fadeInUp": {
                from: {
                  opacity: 0,
                  transform: "translateY(20px)",
                },
                to: {
                  opacity: 1,
                  transform: "translateY(0)",
                },
              },
            }}
          >
            <TrackListItem
              track={track}
              onDelete={onTrackDelete}
              onTrackUpdated={onTrackUpdated}
            />
          </Box>
        ))}
      </Box>
    </Box>
  );
};
