/*export const getGenres = async(): Promise<{data: Track[]; meta:}>=> {
  const response = await axios.get("http://localhost:8000/api/genres");
  return response.data;
};*/

export const createTrack = async (trackData: Partial<Track>): Promise<Track> => {
  const response = await axios.post("http://localhost:8000/api/tracks", trackData);
  return response.data;
};

