import React from "react";
import { Track } from "../api/track/models/Track";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import IconButton from "@mui/material/IconButton";
import CardActions from "@mui/material/CardActions";
import DeleteIcon from "@mui/icons-material/Delete";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";

interface TrackItemProps {
  track: Track;
  onDelete: (trackId: string) => void;
}

export const TrackItem: React.FC<TrackItemProps> = ({ track, onDelete }) => {
  return (
    <Card
      sx={{
        display: "flex",
        flexDirection: "column",
        width: 300,
        borderRadius: 3,
        boxShadow: 3,
        transition: "0.3s",
        "&:hover": { boxShadow: 6, transform: "translateY(-4px)" },
      }}
    >
      {/* Cover Image */}
      <CardMedia
        component="img"
        height="200"
        image={
          track.coverImage ||
          "https://via.placeholder.com/300x200.png?text=No+Cover"
        }
        alt={track.title}
      />

      {/* Content */}
      <CardContent>
        <Typography gutterBottom variant="h6" component="div" noWrap>
          {track.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {track.artist}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {track.album || "Unknown Album"}
        </Typography>

        {/* Audio Player */}
        {track.audioFile && (
          <audio
            controls
            style={{
              width: "100%",
              marginTop: "10px",
              borderRadius: "8px",
            }}
          >
            <source src={track.audioFile} type="audio/mpeg" />
            Ваш браузер не підтримує аудіо.
          </audio>
        )}
      </CardContent>

      {/* Actions */}
      <CardActions sx={{ justifyContent: "space-between" }}>
        <IconButton color="primary">
          <PlayArrowIcon />
        </IconButton>
        <IconButton color="error" onClick={() => onDelete(track.id)}>
          <DeleteIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
};
