import { useEffect, useState } from "react";
import { PageList } from "../types/PageList";
import { deleteTrack, fetchTracks } from "../api/track/trackApi";
import { CreateTrackModal } from "../components/createTrackForm/CreateTrackModal";
import { TrackList } from "../components/TrackList/TrackList";
import { Track } from "../api/track/models/Track";
import { GetModel } from "../api/track/models/CreateTrack";
import { Box, Typography, Button, Paper } from "@mui/material";
import { SearchAppBar } from "../components/SeachBar/SearchBar";
import TablePagination from "@mui/material/TablePagination";
import LibraryMusicIcon from "@mui/icons-material/LibraryMusic";
import { TrackFilterBar } from "../components/TrackFilterSortBar/TrackFilterBar";

const model: GetModel = {
  page: 1,
  limit: 10,
  order: "",
  sort: "",
  genre: "",
  artist: "",
  search: "",
};

export const HomePage = () => {
  const [tracks, setTracks] = useState<PageList<Track>>();
  const [showModal, setShowModal] = useState(false);

  const handleTrackCreated = (newTrack: Track) => {
    if (tracks) {
      setTracks({
        ...tracks,
        data: [newTrack, ...tracks.data],
      });
    }
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks(model);

      if (tracks) {
        setTracks(tracks);
      }
    };

    fetch();
  }, []);

  const handleTrackDelete = async (trackId: string) => {
    try {
      await deleteTrack({ id: trackId });
      setTracks((prev) =>
        prev
          ? { ...prev, data: prev.data.filter((track) => track.id !== trackId) }
          : prev
      );
    } catch (error) {
      console.error(error);
    }
  };

  const handleTrackUpdated = (updatedTrack: Track) => {
    setTracks((prev) =>
      prev
        ? {
            ...prev,
            data: prev.data.map((t) =>
              t.id === updatedTrack.id ? updatedTrack : t
            ),
          }
        : prev
    );
  };
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const handleChangePage = (
    event: React.MouseEvent<HTMLButtonElement> | null,
    newPage: number
  ) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  useEffect(() => {
    const fetch = async () => {
      const tracks = await fetchTracks({
        ...model,
        page: page + 1,
        limit: rowsPerPage,
      });

      if (tracks) {
        setTracks(tracks);
      }
    };
    fetch();
  }, [page, rowsPerPage]);

  return (
    <Box
      sx={{
        minHeight: "100vh",
        background:
          "linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)",
        padding: { xs: "20px", sm: "40px" },
        fontFamily: "'Inter', 'Roboto', sans-serif",
        position: "relative",
        "&::before": {
          content: '""',
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background:
            "radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)",
          pointerEvents: "none",
        },
      }}
    >
      {/* Header */}
      <Paper
        elevation={0}
        sx={{
          display: "flex",
          flexDirection: { xs: "column", sm: "row" },
          alignItems: "center",
          justifyContent: "space-between",
          padding: { xs: "20px", sm: "24px 36px" },
          borderRadius: "24px",
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          mb: 4,
          border: "1px solid rgba(255, 255, 255, 0.3)",
          boxShadow:
            "0 20px 60px rgba(0, 0, 0, 0.15), 0 0 0 1px rgba(255, 255, 255, 0.1) inset",
          position: "relative",
          overflow: "hidden",
          transition: "all 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 25px 70px rgba(0, 0, 0, 0.2)",
          },
          "&::before": {
            content: '""',
            position: "absolute",
            top: 0,
            left: "-100%",
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent)",
            transition: "left 0.5s",
          },
          "&:hover::before": {
            left: "100%",
          },
        }}
      >
        {/* Left Side: Icon + Title */}
        <Box
          sx={{
            display: "flex",
            alignItems: "center",
            gap: 2,
            mb: { xs: 2, sm: 0 },
          }}
        >
          <Box
            sx={{
              width: { xs: 50, sm: 60 },
              height: { xs: 50, sm: 60 },
              borderRadius: "16px",
              background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "0 8px 20px rgba(102, 126, 234, 0.4)",
              transition: "all 0.3s ease",
              "&:hover": {
                transform: "rotate(10deg) scale(1.05)",
              },
            }}
          >
            <LibraryMusicIcon
              sx={{
                fontSize: { xs: 28, sm: 34 },
                color: "#fff",
              }}
            />
          </Box>
          <Box>
            <Typography
              variant="h4"
              sx={{
                fontWeight: 800,
                background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                letterSpacing: "-0.5px",
                fontSize: { xs: "1.5rem", sm: "2rem" },
                lineHeight: 1.2,
              }}
            >
              Track Manager
            </Typography>
            <Typography
              variant="body2"
              sx={{
                color: "#64748b",
                fontSize: "0.875rem",
                mt: 0.5,
              }}
            >
              Керуйте вашою музичною колекцією
            </Typography>
          </Box>
        </Box>

        {/* Right Side: Add Track Button */}
        <Button
          variant="contained"
          onClick={() => setShowModal(true)}
          sx={{
            background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
            fontWeight: 600,
            borderRadius: "16px",
            px: { xs: 3, sm: 4 },
            py: { xs: 1.5, sm: 1.75 },
            textTransform: "none",
            fontSize: { xs: "0.95rem", sm: "1rem" },
            boxShadow: "0 10px 30px rgba(102, 126, 234, 0.4)",
            transition: "all 0.3s ease",
            position: "relative",
            overflow: "hidden",
            "&::before": {
              content: '""',
              position: "absolute",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              background:
                "linear-gradient(135deg, rgba(255,255,255,0.2) 0%, transparent 100%)",
              transform: "translateX(-100%)",
              transition: "transform 0.3s ease",
            },
            "&:hover": {
              transform: "translateY(-3px)",
              boxShadow: "0 15px 40px rgba(102, 126, 234, 0.5)",
              "&::before": {
                transform: "translateX(0)",
              },
            },
            "&:active": {
              transform: "translateY(-1px)",
            },
          }}
        >
          + Додати трек
        </Button>
      </Paper>

      {/* Search + Filter */}
      <Box
        sx={{
          display: "flex",
          flexDirection: { xs: "column", md: "row" },
          alignItems: "stretch",
          gap: 3,
          mb: 4,
        }}
      >
        {/* Search */}
        <Paper
          elevation={0}
          sx={{
            flex: 1,
            padding: { xs: "16px", sm: "20px" },
            borderRadius: "20px",
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            border: "1px solid rgba(255, 255, 255, 0.3)",
            boxShadow: "0 10px 40px rgba(0, 0, 0, 0.1)",
            transition: "all 0.3s ease",
            "&:hover": {
              boxShadow: "0 15px 50px rgba(0, 0, 0, 0.15)",
            },
          }}
        >
          <SearchAppBar
            onSearch={(response) => {
              setTracks(response);
              setPage(0);
            }}
          />
        </Paper>

        {/* Filter */}
        <Paper
          elevation={0}
          sx={{
            width: { xs: "100%", md: "auto" },
            minWidth: { md: 140 },
            borderRadius: "20px",
            padding: "12px 20px",
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            border: "1px solid rgba(255, 255, 255, 0.3)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            boxShadow: "0 10px 40px rgba(0, 0, 0, 0.1)",
            cursor: "pointer",
            transition: "all 0.3s ease",
            "&:hover": {
              transform: "translateY(-2px)",
              boxShadow: "0 15px 50px rgba(0, 0, 0, 0.15)",
            },
          }}
        >
          <TrackFilterBar
            tracks={tracks?.data || []}
            onFilter={(filteredTracks) => {
              if (tracks) {
                setTracks({
                  ...tracks,
                  data: filteredTracks,
                  meta: { ...tracks.meta, total: filteredTracks.length },
                });
              }
            }}
          />
        </Paper>
      </Box>

      {/* Modal */}
      <CreateTrackModal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        onTrackCreated={handleTrackCreated}
      />

      {/* Track List */}
      {tracks?.data.length === 0 ? (
        <Paper
          elevation={0}
          sx={{
            padding: 8,
            borderRadius: "24px",
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            border: "1px solid rgba(255, 255, 255, 0.3)",
            textAlign: "center",
          }}
        >
          <Typography
            variant="h5"
            sx={{
              color: "#94a3b8",
              fontWeight: 500,
              mb: 1,
            }}
          >
            Треків поки немає
          </Typography>
          <Typography
            variant="body1"
            sx={{
              color: "#cbd5e1",
              fontSize: "1rem",
            }}
          >
            Додайте перший трек, щоб почати 🎵
          </Typography>
        </Paper>
      ) : (
        <Box
          sx={{
            "& > *": {
              transition: "all 0.3s ease",
            },
          }}
        >
          <TrackList
            tracks={tracks}
            onTrackDelete={handleTrackDelete}
            onTrackUpdated={handleTrackUpdated}
          />
        </Box>
      )}

      {/* Pagination */}
      <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
        <Paper
          elevation={0}
          sx={{
            borderRadius: "20px",
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            border: "1px solid rgba(255, 255, 255, 0.3)",
            boxShadow: "0 10px 40px rgba(0, 0, 0, 0.1)",
            overflow: "hidden",
          }}
        >
          <TablePagination
            component="div"
            count={tracks?.meta.total || 0}
            page={page}
            onPageChange={handleChangePage}
            rowsPerPage={rowsPerPage}
            onRowsPerPageChange={handleChangeRowsPerPage}
            sx={{
              "& .MuiTablePagination-toolbar": {
                minHeight: 64,
                px: 2,
              },
              "& .MuiTablePagination-selectLabel, & .MuiTablePagination-displayedRows":
                {
                  color: "#64748b",
                  fontWeight: 500,
                },
              "& .MuiIconButton-root": {
                color: "#667eea",
                transition: "all 0.2s ease",
                "&:hover": {
                  background: "rgba(102, 126, 234, 0.1)",
                  transform: "scale(1.1)",
                },
                "&.Mui-disabled": {
                  color: "#cbd5e1",
                },
              },
            }}
          />
        </Paper>
      </Box>
    </Box>
  );
};
